var navToggleBtn=$(".nav-toggle-btn"),navTop=$("nav.navigation-on-top"),navHeight=navTop.innerHeight(),navBody=$(".navigation-on-top > .navigation-body");function closeNavSmaller(){navTop.removeClass("nav-open"),navTop.css("height",navHeight)}function openNavSmaller(){navHeight=navTop.innerHeight(),navTop.addClass("nav-open"),$(".navigation-on-top.nav-open").css("height",navBody.innerHeight())}function registerNavClickOutside(){$(window).on("click",function(n){$(n.target).parents("nav.navigation-on-top").length||closeNavSmaller()})}$(function(){navToggleBtn.click(function(){navTop.hasClass("nav-open")?closeNavSmaller():(openNavSmaller(),registerNavClickOutside())})});